import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import { fetchData } from './omdbAPI';

const initialState = {
    value: [],
    searchValue: '',
    searchLoaded: false,
    status: 'idle'
};

export const updateAsync = createAsyncThunk(
    'search/fetchData',
    async (data) => {
        const response = await fetchData(data);
        return response.json();
    }
);

export const searchSlice = createSlice({
    name: 'search',
    initialState,
    reducers: {
        update: (state, action) => {

        },
        setSearchValue: (state,action) => {
            state.searchValue = action.payload;
        },
        setSearchLoaded: (state, action) => ({
            ...state,
            searchValue: searchValue.concat(action.payload)
        }),
    },
    extraReducers: (builder) => {
      builder
        .addCase(updateAsync.pending, (state) => {
          state.status = 'loading';
        })
        .addCase(updateAsync.fulfilled, (state, action) => {
          state.status = 'idle';
        });
    },
  });

export const { update, setSearchValue, setSearchLoaded} = searchSlice.actions;

export const selectSearchLoaded = (state) => state.search.searchLoaded;
export const selectSearchValue = (state) => state.search.searchValue;

export default searchSlice.reducer;
