import React from 'react';
import styled from 'styled-components'
import { useSelector, useDispatch } from 'react-redux';
import { createAction } from '@reduxjs/toolkit';
import {
    selectSearchLoaded,
    setSearchLoaded,
    selectSearchValue,
    setSearchValue,
    clearSearchValue,
    removeSearchValue,
} from './searchSlice';
import useClickOut from './useClickOut';

const SearchContainer = styled.div`
    width: auto;
    max-width: 100vw;
    height: auto;
    font-size: 24px;
    margin-top: 30px;
    margin-left: 30px;
    display: flex;
    flex-direction: column;
    align-items: flex-start;
`
const SearchBarContainer = styled.div`
    display: flex;
    flex-direction: column;
    box-shadow: 0px 2px 5px lightgrey;
    border-radius: 30px;
    &:hover{
        box-shadow: 0px 2px 5px grey;
    }
`
const SearchBar = styled.textarea`
    width: auto;
    max-width: 40vw;
    height: 1em;
    background: transparent;
    border:none;
    outline: none;
    resize: none;
    overflow: none;
    white-space: nowrap;
    font-size: 24px;
    margin: 10px 20px 10px 15px;
`
const SearchIcon = styled.i`
    margin: 10px 0px 10px 20px;
    transform: scale(0.75) translateY(3px);
    color: darkgrey;
`
const SearchClearIcon = styled.i`
    margin: 10px 0px 15px 0px;
    padding-right: 15px;
    color: grey;
    border-right: 1px darkgrey solid;
    cursor: pointer;
    transform: translateY(2px);
`
const GuessContainer = styled.div`
    // border: 1px solid;
    margin: 0px 20px 20px 20px;
    border-top: 1px solid lightgrey;
    height: 100px;
`
const Search = () => {
    const dispatch = useDispatch();
    const searchLoaded = useSelector(selectSearchLoaded);
    const searchValue = useSelector(selectSearchValue);
    const searchBarRef = React.useRef(null);
    const clearSearchRef = React.useRef(null);

    useClickOut(searchBarRef,clearSearchRef);

    function keyDownHandler(e){
        console.log("wtf",e.key);
        if(e.key.length > 1){
            if(e.key === "Enter"){
                dispatch(setSearchLoaded(false));
            }
            else if(e.key === "Backspace"){
                dispatch(removeSearchValue())
            }
        }
        else{
            dispatch(setSearchValue(e.key));
        }
    }
    function clearSearch(){
        searchBarRef.current.value = "";
        dispatch(clearSearchValue());
    }
    return(
        <SearchContainer>
            {/* <span style={{padding: "20px"}}>MyMovieList</span> */}
            <SearchBarContainer>
                <div style={{display: 'flex'}}>
                    {
                        searchLoaded
                        ?
                        <SearchIcon className="fas fa-search"></SearchIcon>
                        :
                        null
                    } 
                    <SearchBar ref={searchBarRef}
                        onFocus={() => dispatch(setSearchLoaded(true))}
                        // onBlur={() => dispatch(setSearchLoaded(false))}
                        onKeyDown={keyDownHandler}
                        ></SearchBar>
                    {
                        searchValue.length > 0
                        ?
                        <SearchClearIcon 
                            ref={clearSearchRef}
                            className="fas fa-times"
                            onClick={clearSearch}
                        ></SearchClearIcon>
                        :
                        null
                    }
                    <SearchIcon className="fas fa-search"
                        style={{color: 'royalblue', transform: 'scale(1) translateY(2px)', cursor: 'pointer', marginRight: '20px'}}
                    ></SearchIcon>
                </div>
                {
                    false
                    ?
                    <GuessContainer></GuessContainer>
                    :
                    null
                }
            </SearchBarContainer>
        </SearchContainer>
    );
}

export default Search;
