import React from 'react';
import styled from 'styled-components';
import { useSelector, useDispatch } from 'react-redux';
import { selectResult } from './searchSlice';

const ResultContainer = styled.div`
    width: 100vw;
    margin: 0px 30px 5px 30px;
    font-size: 19px;
    display: flex;
`
const Results = () => {
    const results = useSelector(selectResult);

    function loadResults(){
        var r = [];
        results.Search.map((value, i) => (
            r.push(
                <ResultContainer key={i}>
                    <div style={{alignSelf: 'center',display:'flex',flexDirection:'column',fontSize:'20px',margin:'10px'}}>
                        <i class="fas fa-caret-up"></i>
                        <span>0</span>
                        <i class="fas fa-caret-down"></i>
                    </div>
                    <img src={value.Poster} style={{width: '10%'}} ></img>
                    <div style={{display: 'flex',flexDirection:'column'}}>
                        <span style={{margin:"10px 20px 10px 15px", wordWrap: "break-word",width: "100%"}}
                        >{value.Title}</span>
                        <span style={{margin:"10px 20px 10px 15px", fontSize:"15px",color: 'darkgray'}}><b style={{fontSize:"17px"}}>Year: </b>{value.Year}</span>
                    </div>
                </ResultContainer>
            )
        ));
        return r;
    }
    return(
        <div>
            {
                results.Response === 'True'
                ?
                loadResults()
                :
                "No results"
            }
        </div>
    );
}

export default Results;
