import React from 'react';
import styled from 'styled-components'

const SearchContainer = styled.div`
    // width: auto;
    // max-width: 100vw;
    height: auto;
    font-size: 24px;
    margin-top:30px;
`
const SearchBarContainer = styled.div`
    display: flex;
    // width: 200px;
    box-shadow: 0px 2px 5px lightgrey;
    border-radius: 30px;
    &:hover{
        box-shadow: 0px 2px 5px grey;
    }
`
const SearchBar = styled.textarea`
    width: auto;
    max-width: 100px;
    height: 1em;
    background: transparent;
    border:none;
    outline: none;
    resize: none;
    overflow: none;
    white-space: nowrap;
    font-size: 24px;
    margin: 10px 20px 10px 15px;
`
const SearchIcon = styled.i`
    margin: 10px 0px 10px 20px;
    transform: scale(0.75) translateY(3px);
    color: darkgrey;
`
const SearchClearIcon = styled.i`
    margin: 10px 0px 15px 0px;
    padding-right: 15px;
    color: grey;
    border-right: 1px darkgrey solid;
    cursor: pointer;
    transform: translateY(2px);
`
const Search = () => {

    return(
        <SearchContainer>
            {/* <span style={{padding: "20px"}}>MyMovieList</span> */}
            <SearchBarContainer>
                <SearchIcon className="fas fa-search"></SearchIcon>
                <SearchBar></SearchBar>
                <SearchClearIcon className="fas fa-times"></SearchClearIcon>
                <SearchIcon className="fas fa-search"
                    style={{color: 'royalblue', transform: 'scale(1) translateY(2px)', cursor: 'pointer'}}
                ></SearchIcon>
            </SearchBarContainer>
        </SearchContainer>
    );
}

export default Search;
