import React from 'react';
import axios from "axios";
import styled from 'styled-components';
import { useSelector, useDispatch } from 'react-redux';
import { selectResult,selectResultRatings } from './searchSlice';

const ResultContainer = styled.div`
    // width: 100vw;
    margin: 0px 30px 5px 30px;
    font-size: 19px;
    display: flex;
    @media (min-width: 800px) {
        margin: 0px 0px 5px 210px;
    }
`
const VoteIcon = styled.i`
    font-size: 30px;
    cursor: pointer;
    &:hover{
        filter: brightness(1.5);
    }
`
const VoteCount = styled.span`
    font-size: 12px;
    color: grey;
`
const Results = () => {
    const results = useSelector(selectResult);
    const upvoteRef = React.useRef(null);
    const downvoteRef = React.useRef(null);
    const resultRatings = useSelector(selectResultRatings);

    function handleHover(d){

        if(d){
            console.log('fuck you',upvoteRef.current.style.opacity)
            upvoteRef.current.style.display = "1";
        }
        else{
            upvoteRef.current.style.opacity = "0";
        }
    }

    function loadResults(){
        var r = []; 
        results.Search.map((value, i) => {
            console.log('who');

            r.push(
                <ResultContainer key={i}>
                    <div style={{ alignItems:'center',alignSelf: 'center',display:'flex',flexDirection:'column',fontSize:'20px',margin:'15px',color: 'brown'}}>
                        <VoteCount ref={upvoteRef}>{resultRatings[i].upvote}</VoteCount>
                        <VoteIcon className="fas fa-caret-up" onMouseOver={() => handleHover(true)}></VoteIcon>
                        <span>{resultRatings[i].upvote - resultRatings[i].downvote}</span>
                        <VoteIcon className="fas fa-caret-down"></VoteIcon>
                        <VoteCount ref={downvoteRef}>{resultRatings[i].downvote}</VoteCount>
                    </div>
                    <img src={value.Poster} style={{width: '15vh'}} ></img>
                    <div style={{display: 'flex',flexDirection:'column'}}>
                        <span style={{margin:"10px 20px 10px 15px", wordWrap: "break-word",width: "100%"}}
                        >{value.Title}</span>
                        <div>
                            <b style={{margin:"10px 0px 10px 15px",fontSize:'17px',color: 'grey'}}>{value.Type.charAt(0).toUpperCase() + value.Type.slice(1)}</b>
                            <span style={{margin:"10px 20px 10px 10px", fontSize:"15px",color: 'grey'}}><b style={{fontSize:"17px"}}>Year: </b>{value.Year}</span>
                        </div>
                    </div>
                </ResultContainer>
            )
        });
        return r;
    }
    return(
        <div>
            {
                results.Response === 'True'
                ?
                loadResults()
                :
                <ResultContainer>
                    <span style={{  margin:"10px 20px 10px 15px", 
                                    wordWrap: "break-word",
                                    width: "100%"}}>
                        {results.Error}
                    </span>
                </ResultContainer>

            }
        </div>
    );
}

export default Results;
