import React from 'react';
import styled from 'styled-components';
import { useSelector, useDispatch } from 'react-redux';
import { selectResult, selectResultRatings, updateAsyncRatings } from './searchSlice';

const ResultContainer = styled.div`
    // width: 100vw;
    margin: 0px 30px 5px 30px;
    font-size: 19px;
    display: flex;
    @media (min-width: 800px) {
        margin: 0px 0px 5px 210px;
    }
`
const Results = () => {
    const dispatch = useDispatch();
    const results = useSelector(selectResult);
    // const resultRatings = useSelector(selectResultRatings);
    // console.log(resultRatings);

    function loadResults(){
        var r = []; 
        for(let i = 0; i < results.Search.length; i++){
            console.log('WHAT THE FUCK FRANCE');
            dispatch(updateAsyncRatings("http://localhost:3000/movies/"+results.Search[i].imdbID));
        }
        results.Search.map((value, i) => {
            console.log('who');
            r.push(
                <ResultContainer key={i}>
                    <div style={{alignSelf: 'center',display:'flex',flexDirection:'column',fontSize:'20px',margin:'15px'}}>
                        <i class="fas fa-caret-up"></i>
                        <span>{0}</span>
                        <i class="fas fa-caret-down"></i>
                    </div>
                    <img src={value.Poster} style={{width: '15vh'}} ></img>
                    <div style={{display: 'flex',flexDirection:'column'}}>
                        <span style={{margin:"10px 20px 10px 15px", wordWrap: "break-word",width: "100%"}}
                        >{value.Title}</span>
                        <div>
                            <b style={{margin:"10px 0px 10px 15px",fontSize:'17px',color: 'grey'}}>{value.Type.charAt(0).toUpperCase() + value.Type.slice(1)}</b>
                            <span style={{margin:"10px 20px 10px 10px", fontSize:"15px",color: 'grey'}}><b style={{fontSize:"17px"}}>Year: </b>{value.Year}</span>
                        </div>
                    </div>
                </ResultContainer>
            )
        });
        return r;
    }
    return(
        <div>
            {
                results.Response === 'True'
                ?
                loadResults()
                :
                <ResultContainer>
                    <span style={{  margin:"10px 20px 10px 15px", 
                                    wordWrap: "break-word",
                                    width: "100%"}}>
                        {results.Error}
                    </span>
                </ResultContainer>

            }
        </div>
    );
}

export default Results;
