import React from 'react';
import styled from 'styled-components'

const StyledSearch = styled.div`
    width: auto;
    max-width: 100vw;
    height: auto;
    border: 1px black solid;
    display: flex;
    font-size: 24px;
`
const SearchBar = styled.textarea`
    width: auto;
    max-width: 500px;
    height: 1.5em;
    background: transparent;
    border:none;
    outline: none;
    resize: none;
    overflow: none;
    white-space: nowrap;
    font-size: 24px;
`
const Search = () => {

    return(
        <StyledSearch>
            <i className="fas fa-search"></i>
            <SearchBar>

            </SearchBar>
        </StyledSearch>
    );
}

export default Search;
