import React from 'react';
import styled from 'styled-components';
import {useDispatch, useSelector} from 'react-redux'; 
import { updateAsyncRated, selectUserID, selectRated } from './searchSlice';

const Bar = styled.div`
    margin: 10px 30px 5px 30px;
    font-size: 19px;
    display: flex;
    align-items: center;
    justify-content: space-between;
    @media (min-width: 800px) {
        margin: 10px 0px 5px 210px;
    }
`
const VoteIcon = styled.i`
    font-size: 30px;
    cursor: pointer;
    color: ${props => props.vote > 0 ? "gold" : ""};
    ::after{
        font-family:'Nunito', sans-serif;
        position: absolute;
        font-size: 12px;
        content: "${props => props.vote}";
        transform: translateX(3.5px) translateY(10px);
        color: dimgray;
        opacity: 0;
    }
    &:hover{
        filter: brightness(1.5);
        ::after{
            opacity: 1;
        }
    }
`

const Rated = () => {
    const dispatch = useDispatch();
    const userID = useSelector(selectUserID);
    const rated = useSelector(selectRated);

    React.useEffect(() => {
        dispatch(updateAsyncRated(userID));
    }, []);

    return(
        <Bar>
            <span>Movies You've Rated:</span>
            <div style={{display: 'flex', flexDirection: 'column'}}>
                <VoteIcon style={{marginRight: "25px"}} className="fas fa-caret-up"></VoteIcon>
                <VoteIcon className="fas fa-caret-down"></VoteIcon>
            </div>
        </Bar>
    );
}

export default Rated;
