import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import { fetchData } from './omdbAPI';

const initialState = {
    result: false,
    predictions: false,
    searchValue: '',
    searchLoaded: false,
    status: 'idle'
};

export const updateAsync = createAsyncThunk(
    'search/fetchData',
    async (data) => {
        const response = await fetchData(data.data);
        const r = {
            type: data.type,
            response: response.json()
        }
        return r;
    }
);

export const searchSlice = createSlice({
    name: 'search',
    initialState,
    reducers: {
        update: (state, action) => {

        },
        setSearchValue: (state,action) => ({
            ...state,
            searchValue: state.searchValue.concat(action.payload)
        }),
        removeSearchValue: (state) => ({
            ...state,
            searchValue: state.searchValue.substring(0, state.searchValue.length > 0 ?
                                                        state.searchValue.length-1 : 0)
        }),
        setSearchLoaded: (state, action) => {
            state.searchLoaded = action.payload;
        },
        clearSearchValue: (state) => {
            state.searchValue = '';
        },
        clearPredictions: (state) =>{
            state.predictions = false;
        }
    },
    extraReducers: (builder) => {
      builder
        .addCase(updateAsync.pending, (state) => {
          state.status = 'loading';
        })
        .addCase(updateAsync.fulfilled, (state, action) => {
          state.status = 'idle';
          if(action.payload.type === "predictions"){
            state.predictions = action.payload.response;
          }
          else if(action.payload.type === "result"){
            state.result = action.payload.response;
          }

        });
    },
  });

export const { update, setSearchValue, setSearchLoaded, clearSearchValue, removeSearchValue, clearPredictions} = searchSlice.actions;

export const selectPredictions = (state) => state.search.predictions;
export const selectSearchLoaded = (state) => state.search.searchLoaded;
export const selectSearchValue = (state) => state.search.searchValue;

export default searchSlice.reducer;
