import React from 'react';
import styled from 'styled-components';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Search from './components/Search';
import Results from './components/Results';

const Header = styled.div`
  width: auto;
  max-width: calc(100vw - 60px);
  height: 100px;
  font-size: 24px;
  padding: 30px 30px 20px 30px;
  display: flex;
  flex-direction: column;
  position: relative;
  .mobile{
    color: gold;
    margin-right: 20px;
    cursor: pointer;
  }
  .desktop{
    display: none;
    color: gold;
    cursor: pointer;
  }
  .title{
    width:100%;
    display:flex;
    justify-content:space-between;
  }
  @media (min-width: 800px) {
    flex-direction: row;
    padding: 10px 30px 20px 30px;
    align-items: start;
    .mobile{
      display:none;
    }
    .desktop{
      display: block;
      margin-left: calc(100vw - 45vw - 282px);
      margin-right: 10px;
    }
    .title{
      width: 185px;
      display:flex;
      justify-content: flex-start;
    }
  }
`
function App() {
  return (
    <Router>
      <Switch>
        <Route Path="/">
          <Header>
            <div className="title">
                <div style={{paddingLeft: '10px'}}>
                    <i style={{ marginRight: "5px"}} className="fas fa-film"></i>
                    <span style={{fontFamily: "'Nunito', sans-serif"}}>MyMovieList</span>
                </div>
                <i className="fas fa-star mobile"></i>
            </div>
            <Search></Search>
            <i className="fas fa-star desktop"></i>
          </Header>
          <Results></Results>
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
