import React from 'react';
import styled from 'styled-components';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Search from './components/Search';
import Results from './components/Results';

const Header = styled.div`
  width: auto;
  max-width: calc(100vw - 60px);
  height: 100px;
  font-size: 24px;
  padding: 30px 30px 20px 30px;
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
  @media (min-width: 800px) {
    flex-direction: row;
    .mobile{
      opacity: 0;
    }
    .title{
      justify-content: flex-start;
    }
  }
`
function App() {
  return (
    <Router>
      <Switch>
        <Route Path="/">
          <Header>
            <div className="title" style={{width: "100%",display: 'flex', justifyContent: 'space-between'}}>
                <div style={{paddingLeft: '10px'}}>
                    <i style={{ marginRight: "5px"}} className="fas fa-film"></i>
                    <span style={{fontFamily: "'Nunito', sans-serif"}}>MyMovieList</span>
                </div>
                <i style={{color: 'gold', paddingRight: '20px'}} className="fas fa-star mobile"></i>
            </div>
            <Search></Search>
            <i style={{color: 'gold'}} className="fas fa-star desktop"></i>
          </Header>
          <Results></Results>
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
