import React from 'react';
import styled from 'styled-components';
import { useSelector, useDispatch } from 'react-redux';
import { selectResult } from './searchSlice';

const ResultContainer = styled.div`
    width: 100vw;
    margin: 0px 30px 5px 30px;
    font-size: 19px;
    display: flex;
`
const Results = () => {
    const results = useSelector(selectResult);

    function loadResults(){
        var r = [];
        results.Search.map((value, i) => (
            r.push(
                <ResultContainer key={i}>
                    <div style={{display:'flex',flexDirection:'column',marginLeft:'10px'}}>
                        <i style={{fontSize:'24px'}} class="fas fa-caret-up"></i>
                        <span style={{fontSize:'15px'}}>0</span>
                        <i style={{fontSize:'24px'}} class="fas fa-caret-down"></i>
                    </div>
                    <span style={{margin:"10px 20px 10px 15px", wordWrap: "break-word",width: "100%"}}>{value.Title}</span>
                </ResultContainer>
            )
        ));
        return r;
    }
    return(
        <div>
            {
                results.Response === 'True'
                ?
                loadResults()
                :
                "No results"
            }
        </div>
    );
}

export default Results;
