import React from 'react';
import styled from 'styled-components';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import Search from './components/Search';
import Results from './components/Results';

const Header = styled.div`
  width: auto;
  max-width: calc(100vw - 60px);
  height: 100px;
  font-size: 24px;
  padding: 30px 30px 20px 30px;
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
  .mobile{
    color: gold;
    margin-right: 20px;
    cursor: pointer;
    &:hover{
      filter: brightness(1.1);
    }
  }
  .desktop{
    display: none;
    color: gold;
    cursor: pointer;
    margin:20px 0px 20px 0px;
    &:hover{
      filter: brightness(1.1);
    }
  }
  .title{
    width:100%;
    display:flex;
    justify-content:space-between;
  }
  @media (min-width: 800px) {
    flex-direction: row;
    align-items: flex-start;
    height: 70px;
    .mobile{
      display:none;
    }
    .desktop{
      display: block;
      margin-left: calc(100vw - 50vw - 282px);
      margin-right: 10px;
    }
    .title{
      width: 185px;
      display:flex;
      justify-content: flex-start;
      margin:15px 0px 25px 0px;
    }
  }
`
function App() {
  return (
    <Router>
      <Switch>
        <Route Path="/rated">

        </Route>
        <Route Path="/">
          <Header>
            <div className="title">
                <Link style={{textDecoration: 'none'}}  to="/">
                  <div style={{paddingLeft: '10px'}}>
                      <i style={{ marginRight: "5px"}} className="fas fa-film"></i>
                      <span style={{fontFamily: "'Nunito', sans-serif"}}>MyMovieList</span>
                  </div>
                </Link>
                <Link style={{textDecoration: 'none'}} to="/rated">
                  <i className="fas fa-star mobile"></i>
                </Link>
            </div>
            <Search></Search>
            <Link style={{textDecoration: 'none'}}  to="/rated">
              <i className="fas fa-star desktop"></i>
            </Link>
          </Header>
          <Results></Results>
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
