import React from 'react';
import styled from 'styled-components'

const SearchContainer = styled.div`
    width: auto;
    max-width: 100vw;
    height: auto;
    // border: 1px black solid;
    font-size: 24px;
`
const SearchBarContainer = styled.div`
    display: flex;
`
const SearchBar = styled.textarea`
    width: auto;
    max-width: 500px;
    height: 1.5em;
    background: transparent;
    border:none;
    outline: none;
    resize: none;
    overflow: none;
    white-space: nowrap;
    font-size: 24px;
    padding: 10px;
`
const SearchIcon = styled.i`
    padding: 10px 0px 10px 0px;
    transform: scale(0.75);
    color: darkgrey;
`
const SearchClearIcon = styled.i`
    padding: 10px;
    transform: scale(0.75);
    color: grey;
    border-right: 1px darkgrey solid;
`
const Search = () => {

    return(
        <SearchContainer>
            <SearchBarContainer>
                <span style={{padding: "20px"}}>MyMovieList</span>
                <SearchIcon className="fas fa-search"></SearchIcon>
                <SearchBar></SearchBar>
                <SearchClearIcon className="fas fa-times"></SearchClearIcon>
                {/* <span style={{padding: "20px", color: "darkgrey"}}>|</span> */}
            </SearchBarContainer>
        </SearchContainer>
    );
}

export default Search;
